import React from "react";

export default function Die(props) {
    const styles = {
        backgroundColor: props.isHeld ? "#59E391" : "white"
    };

    const renderDots = () => {
        const dotPositions = [
            [],
            [4],
            [0, 8],
            [0, 4, 8],
            [0, 2, 6, 8],
            [0, 2, 4, 6, 8],
            [0, 2, 3, 5, 6, 8]
        ];

        return dotPositions[props.value - 1].map((position) => (
            <div key={position} className="dot" style={{ gridArea: `dot${position}` }}></div>
        ));
    };

    return (
        <div 
            className="die-face" 
            style={styles}
            onClick={props.holdDice}
        >
            {renderDots()}
        </div>
    );
}
